
public class UsoBucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<10;i++) {
			System.out.println("CArlos "+i);
		}
		
		for (int j=10;j>0;j--) {
			System.out.println("CArlos "+j);
		}
		
		for (int i=0;i<20;i+=2) {
			System.out.println("CArlos "+i);
		}

	}

}
